mapper.select:
SELECT su.* FROM bh_course_info su WHERE su.enabled_flag = 'Y'
AND su.class_type like CONCAT('%',#{classType,jdbcType=VARCHAR},'%')
AND su.class_name = #{className,jdbcType=VARCHAR}
limit #{start},#{limit}

SELECT count(1) FROM bh_course_info su WHERE su.enabled_flag = 'Y'
AND su.class_type like CONCAT('%',#{classType,jdbcType=VARCHAR},'%')
AND su.class_name = #{className,jdbcType=VARCHAR}
