mapper.select:
select * from bh_permission sp
where  enabled_flag = 'Y'
and sp.permission_code =  #{permissionCode,jdbcType=VARCHAR}

mapper.delete:
DELETE FROM  bh_role_permission
WHERE permission_id IN

  #{item.permissionId,jdbcType=INTEGER}

mapper.select:
select sp.* ,ps.permission_name AS parentName  from bh_permission sp
LEFT JOIN bh_permission ps ON sp.permission_id = ps.parent_id
where sp.enabled_flag = 'Y'

  and sp.permission_name like CONCAT('%', #{permissionName,jdbcType=VARCHAR},'%')


  and sp.permission_type = #{permissionType,jdbcType=VARCHAR}


  and sp.permission_id = #{permissionId,jdbcType=INTEGER}


  and  sp.parent_id = #{parentId,jdbcType=INTEGER}

order by permission_sn
limit #{start},#{limit}

select count(1) from bh_permission sp
where sp.enabled_flag = 'Y'

  and sp.permission_name like CONCAT('%', #{permissionName,jdbcType=VARCHAR},'%')


  and sp.permission_type = #{permissionType,jdbcType=VARCHAR}


  and sp.permission_id = #{permissionId,jdbcType=INTEGER}


  and  sp.parent_id = #{parentId,jdbcType=INTEGER}

select sp.* from bh_permission sp
where sp.enabled_flag = 'Y'

  and sp.permission_name like CONCAT('%', #{permissionName,jdbcType=VARCHAR},'%')


  and sp.permission_type = #{permissionType,jdbcType=VARCHAR}


  and sp.permission_id = #{permissionId,jdbcType=INTEGER}


  and  sp.parent_id = #{parentId,jdbcType=INTEGER}

order by permission_sn
