mapper.update:
update bh_role
set role_name = #{roleName,jdbcType=VARCHAR},
  memo = #{memo,jdbcType=VARCHAR},
  enabled_flag = #{enabledFlag,jdbcType=VARCHAR},
  update_time = #{updateTime,jdbcType=TIMESTAMP}
where role_id = #{roleId,jdbcType=INTEGER}

mapper.select:
SELECT su.* FROM bh_role su WHERE su.enabled_flag = 'Y'
AND su.role_name like CONCAT('%',#{roleName,jdbcType=VARCHAR},'%')
order by role_id limit #{start},#{limit}

mapper.delete:
delete from bh_user_role where
role_id = #{roleId,jdbcType=INTEGER}
 AND  user_id in

  #{item}
